class AccountInvoice extends BaseAccountInvoice {

  public function fetchRecent() {
    return Doctirne_Query::create()
             ->from('AccountInvoice ai')
             ->orderBy('ai.updated_at DESC')
             ->limit(5)
             ->execute();
  }
